import {useState} from 'react';
import {useEffect} from 'react';
const FeaturedProducts = ({ darkMode }) => {
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);
  
    useEffect(() => {
      const dummyProducts = [
        {
          id: 1,
          name: 'Premium Hoodie',
          price: 65,
          category: 'Clothing',
          image: 'data:image/jpeg;base64,',
          description: 'Soft cotton hoodie with premium finish'
        },
        {
          id: 2,
          name: 'Slim Fit Jeans',
          price: 89,
          category: 'Clothing',
          image: 'data:image/jpeg;base64,',
          description: 'Stretch denim with modern fit'
        },
        {
          id: 3,
          name: 'Leather Backpack',
          price: 149,
          category: 'Accessories',
          image: 'data:image/jpeg;base64,',
          description: 'Full-grain leather backpack'
        },
        {
          id: 4,
          name: 'Running Shoes',
          price: 120,
          category: 'Footwear',
          image: 'data:image/jpeg;base64,',
          description: 'Lightweight performance runners'
        }
      ];
      setProducts(dummyProducts);
    }, []);
  
    const addToCart = (product) => {
      setCart([...cart, { ...product, quantity: 1 }]);
    };
  
    return (
      <div className="container py-5">
        <h2 className={`mb-4 ${darkMode ? 'text-light' : 'text-dark'}`}>Featured Products</h2>
        <div className="row g-4">
          {products.map((product) => (
            <div key={product.id} className="col-12 col-sm-6 col-md-4 col-lg-3">
              <div className={`card h-100 border-0 shadow-sm ${darkMode ? 'bg-secondary text-light' : ''}`}>
                <img src={product.image} className="card-img-top p-3" alt={product.name} 
                  style={{ objectFit: 'contain', height: '250px' }} />
                <div className="card-body">
                  <div className="d-flex justify-content-between align-items-center mb-2">
                    <h5 className="card-title mb-0">{product.name}</h5>
                    <span className={`badge ${darkMode ? 'bg-dark' : 'bg-light text-dark'}`}>
                      ${product.price}
                    </span>
                  </div>
                  <p className="small text-muted mb-2">{product.category}</p>
                  <p className="card-text">{product.description}</p>
                </div>
                <div className="card-footer bg-transparent border-top-0">
                  <button 
                    className={`btn ${darkMode ? 'btn-light' : 'btn-primary'} w-100`}
                    onClick={() => addToCart(product)}>
                    Add to Cart
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };
export default FeaturedProducts;